def test_source ():
    path_results='test_source'
    def add1 (x): return x+1
    component = Component (add1, path_results=path_results)
    x=1
    assert component(x)==2
    assert os.path.exists (f'{path_results}/whole/add1_result.pk')
    s = Source (path_results=path_results, name='add1')
    assert s()==2
    os.rename (f'{path_results}/whole/add1_result.pk', f'{path_results}/add1.pk')
    shutil.rmtree (f'{path_results}/whole')
    assert os.path.exists (f'{path_results}/add1.pk')
    s2 = Source (path=path_results, name='add1')
    assert s2 ()==2
    remove_previous_results (path_results)
    
    df = pd.DataFrame (dict(x=[1,2,3],
                            y=[10,20,30]))
    component2 = Component (add1, path_results=path_results, result_io='csv')
    _ = component2 (df)
    pd.testing.assert_frame_equal (pd.read_csv(f'{path_results}/whole/add1_result.csv', index_col=0), df+1)
    s = Source (path_results=path_results, name='add1', result_io='csv')
    pd.testing.assert_frame_equal (s(), df+1)
    os.rename (f'{path_results}/whole/add1_result.csv', f'{path_results}/add1.csv')
    shutil.rmtree (f'{path_results}/whole')
    assert os.path.exists (f'{path_results}/add1.csv')
    s2 = Source (path=path_results, name='add1', result_io='csv')
    pd.testing.assert_frame_equal (s2(), df+1)
    
    remove_previous_results (path_results)


