def test_multi_split_source ():
    path_results='test_multi_split_source'
    def add1 (x): return x+1
    df = pd.DataFrame (dict(x=[1,2,3],
                            y=[10,20,30]))
    component2 = Component (add1, path_results=path_results, result_io='csv')
    _ = component2(df)
    
    p=component2.data_io.get_path_result_file()
    p.rename (component2.path_results / 'train.csv')
    assert (component2.path_results / 'train.csv').exists()
    
    component2(df+1)
    p.rename (component2.path_results / 'test.csv')
    assert (component2.path_results / 'test.csv').exists()
    
    remove_previous_results (component2.path_results / 'whole')
    
    assert sorted(os.listdir (component2.path_results))==sorted(['train.csv', 'test.csv'])
    multi_split_source = MultiSplitSource (path=path_results, result_io='csv')
    data_dict=multi_split_source ()
    pd.testing.assert_frame_equal (data_dict['train'],df+1)
    pd.testing.assert_frame_equal (data_dict['test'],df+2)
    
    remove_previous_results (path_results)


