from dataclasses import dataclass

from routeviews import peeringdb, types


# can pretty print to console easily.
# can be injested by 'ansible.CollectorConfig'.
# Generated by any 'peeringdb.Network.peer_with(other_network)'
@dataclass
class PeerRequest:
    my_network: 'peeringdb.Network'
    my_address: types.IPAddr
    your_network: 'peeringdb.Network'
    your_address: types.IPAddr
